mapper.update:
update messages
set user_id = #{userId,jdbcType=VARCHAR},
  title = #{title,jdbcType=VARCHAR},
  content = #{content,jdbcType=LONGVARCHAR},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, user_id, title, content, status
from messages
where id = #{id,jdbcType=VARCHAR}

select id, user_id, title, content, status
from messages
where user_id = #{userId}

select status
from message_status
where id=#{statusId}

mapper.update:
update messages
set status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, user_id, title, content, status from messages
title like concat("%",#{detail},"%") or content like concat("%",#{detail},"%")
limit #{page}, #{limit}
