mapper.update:
update return_records
set borrow_id = #{borrowId,jdbcType=VARCHAR},
  return_date = #{returnDate,jdbcType=TIMESTAMP},
  status = #{status,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, borrow_id, return_date, status
from return_records
where id = #{id,jdbcType=VARCHAR}
